let newschedule = [];
oldschedule.forEach(element => {
    let newindex = null;
    if(newschedule.length > 0){
        for(i=0;i<newschedule.length-1;i++){
            if(element.time >= newschedule[i].time && element.time < newschedule[i+1].time){
                newindex = i+1;
            }
        }
        if(newindex !== null){
            newschedule.splice(newindex,0,element);
        } else if (element.time < newschedule[0].time){
            newschedule.splice(0,0,element);
        } else {
            newschedule.push(element);
        }
    } else {
        newschedule.push(element);
    }
});
