let errorMsg = "";
if(!Array.isArray(msg.payload)) {
    errorMsg = "msg.payload should be an array of schedule items";
    node.warn(errorMsg)
    msg.payload = errorMsg;
    return [null, msg];
}
for(i=0;i<msg.payload.length;i++){
    if(typeof msg.payload[i] !== "object") {
        errorMsg = "each array item should be an object";
        node.warn(errorMsg)
        msg.payload = errorMsg;
        return [null, msg];
    }
    if(!msg.payload[i].hasOwnProperty("item") || !msg.payload[i].hasOwnProperty("command") || !msg.payload[i].hasOwnProperty("time")) {
        errorMsg = "each array item should contain a item, a command and time property";
        node.warn(errorMsg)
        msg.payload = errorMsg;
        return [null, msg];
    }
    if(typeof msg.payload[i].item !== "string") {
        errorMsg = "the items in each schedule should be given as a string";
        node.warn(errorMsg)
        msg.payload = errorMsg;
        return [null, msg];
    }
    if(typeof msg.payload[i].command !== "string" && typeof msg.payload[i].command !== "number" && typeof msg.payload[i].command !== "boolean") {
        errorMsg = "the commands in each schedule should be given as a string or a number";
        node.warn(errorMsg)
        msg.payload = errorMsg;
        return [null, msg];
    }
    if(!msg.payload[i].time.match(/[0-2]\d\:[0-5]\d/g)) {
        errorMsg = "the time should be in hh:mm 24 hour format";
        node.warn(errorMsg)
        msg.payload = errorMsg;
        return [null, msg];
    }
    if(msg.payload[i].hasOwnProperty("days")) {
        if(!Array.isArray(msg.payload[i].days)) {
            errorMsg = "days should be given as an array of integers";
            node.warn(errorMsg)
            msg.payload = errorMsg;
            return [null, msg];
        }
        for(let c=0; c<msg.payload[i].days.length; c++){
            if(typeof msg.payload[i].days[c] !== "number"){
                errorMsg = "days should be given as integers of type number";
                node.warn(errorMsg)
                msg.payload = errorMsg;
                return [null, msg];
            }
            if(msg.payload[i].days[c] < 1 || msg.payload[i].days[c] > 7){
                errorMsg = "days should be in the range of 1-7";
                node.warn(errorMsg)
                msg.payload = errorMsg;
                return [null, msg];
            }
        }
    }
}
return [msg, null];

