const { Base64Encode } = base64Encode

async function getPDFBase64Text(pdfDoc) {
  return new Promise((resolve) => {
    var base64String = ""
    const stream = pdfDoc.pipe(new Base64Encode())
    pdfDoc.end()

    stream.on("data", (chunk) => {
      base64String += chunk
    })

    stream.on("end", () => resolve(base64String))
  })
}

